sys_ui_page.client_script:
var app=angular.module("myApp2",[]);
app.controller("myCtrl",function($scope){
	//Init UI States
	$scope.agtName=g_user.getFullName();
	$scope.showBtnLogin=false;
	$scope.showBtnReady=false;
	$scope.showBtnPause=false;
	$scope.showBtnLogout=false;
	$scope.currentState="Logout";

	//Init State Manager
	var stateMap={"Login":["Ready"],"Ready":["Pause","Logout"],"Pause":["Ready","Logout"],			"Logout":["Login"]};
	function setState(newState){
		if ( stateMap[$scope.currentState].indexOf(newState)!= -1){
			switch (newState){
					case "Login":
						$scope.currentState="Login";
						break;
					case "Ready":
						$scope.currentState="Ready";
						break;
					case "Pause":
						$scope.currentState="Pause";
						break;
					case "Logout":
						$scope.currentState="Logout";
						break;
			}
			refreshUI();
		}else{			alert("Invalid newState:"+newState+"for currentstate:"+$scope.currentState);		}
	}
	function refreshUI(){
		for (var state in stateMap){			$scope["showBtn"+state] = stateMap[$scope.currentState].indexOf(state)!=-1;		}
	}
	refreshUI();

	$scope.clickedLogin=function(){setState("Login");};
	$scope.clickedReady=function(){setState("Ready");};
	$scope.clickedPause=function(){setState("Pause");};
	$scope.clickedLogout=function(){setState("Logout");};

});
